import React, { useState, useRef, useEffect } from 'react';
import { PaperAirplaneIcon } from '@heroicons/react/24/solid';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Добавляем сообщение пользователя
    const userMessage = {
      type: 'user',
      content: input,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');

    try {
      // Здесь будет запрос к внешнему API
      // Пример ответа для демонстрации
      const mockResponse = {
        explanation: "Найдены релевантные фрагменты кода",
        code_snippets: [
          {
            file: "example.js",
            code: "function example() {\n  return 'Hello World';\n}",
            relevance: 0.95
          }
        ]
      };

      // Добавляем ответ агента
      const agentMessage = {
        type: 'agent',
        content: mockResponse.explanation,
        codeSnippets: mockResponse.code_snippets,
        timestamp: new Date().toISOString()
      };

      setMessages(prev => [...prev, agentMessage]);
    } catch (error) {
      console.error('Error:', error);
      // Добавляем сообщение об ошибке
      setMessages(prev => [...prev, {
        type: 'error',
        content: 'Произошла ошибка при обработке запроса',
        timestamp: new Date().toISOString()
      }]);
    }
  };

  return (
    <div className="flex flex-col h-full">
      {/* Заголовок чата */}
      <div className="bg-white shadow-sm p-4">
        <h2 className="text-lg font-semibold text-gray-800">Чат с AI-агентом</h2>
      </div>

      {/* Область сообщений */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`flex ${
              message.type === 'user' ? 'justify-end' : 'justify-start'
            }`}
          >
            <div
              className={`max-w-[70%] rounded-lg p-3 ${
                message.type === 'user'
                  ? 'bg-primary-500 text-white'
                  : message.type === 'error'
                  ? 'bg-red-100 text-red-800'
                  : 'bg-gray-100 text-gray-800'
              }`}
            >
              <p className="text-sm">{message.content}</p>
              {message.codeSnippets && (
                <div className="mt-2 space-y-2">
                  {message.codeSnippets.map((snippet, idx) => (
                    <div key={idx} className="bg-gray-800 text-white p-2 rounded">
                      <p className="text-xs text-gray-400">{snippet.file}</p>
                      <pre className="text-sm mt-1 overflow-x-auto">
                        <code>{snippet.code}</code>
                      </pre>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Форма ввода */}
      <form onSubmit={handleSubmit} className="bg-white border-t p-4">
        <div className="flex space-x-2">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Введите ваш запрос..."
            className="flex-1 rounded-lg border border-gray-300 px-4 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
          />
          <button
            type="submit"
            className="bg-primary-500 text-white rounded-lg px-4 py-2 hover:bg-primary-600 focus:outline-none focus:ring-2 focus:ring-primary-500"
          >
            <PaperAirplaneIcon className="h-5 w-5" />
          </button>
        </div>
      </form>
    </div>
  );
}

export default Chat;
